$(document).ready(function(){chrome.system.cpu.getInfo(function t(d){$("<tr><td>CPU内核数量</td><td>"+d.numOfProcessors+"</td></tr>").appendTo($("table.cpuInfo tbody"));$("<tr><td>CPU Model 名称</td><td>"+d.modelName+"</td></tr>").appendTo($("table.cpuInfo tbody"));$("<tr><td>CPU Arch</td><td>"+d.archName+"</td></tr>").appendTo($("table.cpuInfo tbody"));$.each(d.processors,function(t,d){$('<tr class="success"><td colspan="2">第'+t+"个CPU内核信息</td></tr>").appendTo($("table.cpuInfo tbody"));$("<tr><td>CPU空闲线程</td><td>"+d.usage.idle+"</td></tr>").appendTo($("table.cpuInfo tbody"));$("<tr><td>CPU内核线程</td><td>"+d.usage.kernel+"</td></tr>").appendTo($("table.cpuInfo tbody"));$("<tr><td>CPU用户线程</td><td>"+d.usage.user+"</td></tr>").appendTo($("table.cpuInfo tbody"));$("<tr><td>CPU总线程数</td><td>"+d.usage.total+"</td></tr>").appendTo($("table.cpuInfo tbody"))})});chrome.system.memory.getInfo(function d(t){$("<tr><td>内存可用量</td><td>"+t.availableCapacity+"</td></tr>").appendTo($("table.memInfo tbody"));$("<tr><td>内存总量</td><td>"+t.capacity+"</td></tr>").appendTo($("table.memInfo tbody"))})});